import { createSlice } from '@reduxjs/toolkit';
i
const initialState = {
  messages: [],
  isLoading: true,
};

const messagesSlice = createSlice({
  name: 'messages',
  initialState,
  reducers: {
    setMessages(state, { payload }) {
      state.messages = payload;
    },
  },
});

export const { setMessages } = messagesSlice;
export default messagesSlice.reducer;
