import { useEffect, useContext } from 'react';
import { Formik, Form, ErrorMessage, Field } from 'formik';
import * as Yup from 'yup';
import { SocketContext } from '../context/socket';
import { useAuth } from '../context/auth';

const MessagesForm = ({ currentChannelId }) => {
  const socket = useContext(SocketContext);
  const { username } = useAuth();

  return (
    <div className="mt-auto px-5 py-3">
      <Formik
        initialValues={{ body: '' }}
        onSubmit={async ({body}, { resetForm }) => {

          if (body) {
            try {
              await socket.emit('newMessage', {
                body: body,
                channelId: currentChannelId,
                username: username,
              });
              resetForm();
            } catch (error) {
              console.log(error.message);
            }
          }
        }}>
        <Form>
          <div className="input-group has-validation">
            <Field
              id="body"
              name="body"
              aria-label="Новое сообщение"
              placeholder="Введите сообщение..."
              className="border-0 p-0 ps-2 form-control"
            />
            <button
              type="submit"
              className="btn btn-group-vertical"
              disabled="">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                viewBox="0 0 16 16"
                width="20"
                height="20"
                fill="currentColor">
                <path
                  fillRule="evenodd"
                  d="M15 2a1 1 0 0 0-1-1H2a1 1 0 0 0-1 1v12a1 1 0 0 0 1 1h12a1 1 0 0 0 1-1V2zM0 2a2 2 0 0 1 2-2h12a2 2 0 0 1 2 2v12a2 2 0 0 1-2 2H2a2 2 0 0 1-2-2V2zm4.5 5.5a.5.5 0 0 0 0 1h5.793l-2.147 2.146a.5.5 0 0 0 .708.708l3-3a.5.5 0 0 0 0-.708l-3-3a.5.5 0 1 0-.708.708L10.293 7.5H4.5z"></path>
              </svg>
              <span className="visually-hidden">Отправить</span>
            </button>
          </div>
        </Form>
      </Formik>
    </div>
  );
};
export default MessagesForm;
