import React, { useState } from 'react';

import cn from 'classnames';
import Button from 'react-bootstrap/esm/Button';

const Channel = ({ name, removable, id, currentChannelId, handleChoose }) => {
  const [isOpen, setIsOpen] = useState(false);

  const btnClass = cn('w-100', 'rounded-0', 'text-start', 'btn', {
    'btn-secondary': id === currentChannelId,
  });
  const chowClass = cn('d-flex', 'dropdown', 'btn-group', {
    show: isOpen,
  });
  const chowClassToggle = cn('dropdown-menu', {
    show: isOpen,
  });

  const onClickHandler = () => setIsOpen((prev) => !prev);
  const onBlurHandler = () => setIsOpen((prev) => !prev);
  const onFocusHandler = () => setIsOpen((prev) => !prev);

  if (removable) {
    return (
      <div role="group" className={chowClass}>
        <button
          type="button"
          onClick={handleChoose}
          className={btnClass}
          id={id}>
          <span className="me-1">#</span>
          {name}
        </button>
        <ButtonGroup aria-label="Basic example" aria-labelledby="basic-example">
          <Button>Left</Button>
          <Button>Middle</Button>
          <Button>Right</Button>
        </ButtonGroup>
        <button
          onClick={onClickHandler}
          type="button"
          id="react-aria5523499611-2"
          aria-expanded={isOpen}
          className="flex-grow-0 dropdown-toggle dropdown-toggle-split btn btn-secondary">
          <span className="visually-hidden">Управление каналом</span>
        </button>
        <div
          x-placement="bottom-start"
          aria-labelledby="react-aria5523499611-2"
          className={chowClassToggle}
          data-popper-reference-hidden="false"
          data-popper-escaped="false"
          data-popper-placement="bottom-start">
          <a
            data-rr-ui-dropdown-item=""
            className="dropdown-item"
            role="button"
            tabIndex="0"
            href="#">
            Удалить
          </a>
          <a
            data-rr-ui-dropdown-item=""
            className="dropdown-item"
            role="button"
            tabIndex="0"
            href="#">
            Переименовать
          </a>
        </div>
      </div>
    );
  }
  return (
    <>
      <button type="button" onClick={handleChoose} id={id} className={btnClass}>
        <span className="me-1">#</span>
        {name}
      </button>
    </>
  );
};

export default Channel;
