import { useState, useContext, createContext } from 'react';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [username, setUsername] = useState(usernameFromLocalStorage);
  const navigate = useNavigate();
  const { username: usernameFromLocalStorage } = JSON.parse(localStorage.user);

  // const login = () => {
  //   console.log(usernameFromLocalStorage, 'usernameFromLocalStorage');
  //   setUsername(usernameFromLocalStorage);
  // };
  const logout = () => {
    localStorage.user = '';
    navigate('/login');
  };

  return (
    <AuthContext.Provider value={{ logout, username,  }}>{children}</AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
