import React from "react";
import { useSelector } from 'react-redux';
import Channel from "./Channel";

const Channels = () => {

    const { channels, currentChannelId } = useSelector((store) => store.channelsReducer);
    // console.log(channels, currentChannelId );
    return (
        <>
            <ul className="nav flex-column nav-pills nav-fill px-2">
              {channels.map((channel) => (<li className="nav-item w-100" key={channel.id}>
                 <Channel  {...channel} currentChannelId = {currentChannelId}/>
                 </li>
                 ))}
            </ul>
        </>
    )
}


export default Channels;
