import React, { useState } from 'react';
import cn from 'classnames';
import Button from 'react-bootstrap/Button';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import Dropdown from 'react-bootstrap/Dropdown';


const Channel = ({ name, removable, id, currentChannelId, handleChoose }) => {
  const [isOpen, setIsOpen] = useState(false);

  const btnClass = cn('w-100', 'rounded-0', 'text-start', 'btn', {
    'btn-secondary': id === currentChannelId,
  });
  const chowClass = cn('d-flex', 'dropdown', 'btn-group', {
    show: isOpen,
  });
  const chowClassToggle = cn('dropdown-menu', {
    show: isOpen,
  });

  const onClickHandler = () => setIsOpen((prev) => !prev);
  const onBlurHandler = () => setIsOpen((prev) => !prev);
  const onFocusHandler = () => setIsOpen((prev) => !prev);

  if (removable) {
    return (

    <Dropdown as={ButtonGroup}  variant="secondary">
      <Button variant="secondary" onClick={handleChoose} id={id}>{name}</Button>

      <Dropdown.Toggle split variant="btn-secondary" id="dropdown-split-basic" />

      <Dropdown.Menu>
        <Dropdown.Item href="#/action-1">Удалить</Dropdown.Item>
        <Dropdown.Item href="#/action-2">Переименовать</Dropdown.Item>
      </Dropdown.Menu>
    </Dropdown>

    );
  }
  return (
    <>
      <button type="button" onClick={handleChoose} id={id} className={btnClass}>
        <span className="me-1">#</span>
        {name}
      </button>
    </>
  );
};

export default Channel;
