import { createSlice } from '@reduxjs/toolkit';
const initialState = {
    channels:[
        {
            "id": 1,
            "name": "general",
            "removable": false
        },
        {
            "id": 2,
            "name": "random",
            "removable": false
        }
    ],
    currentChannelId:1,
    isLoading: true,
};

const channelsSlice = createSlice({
    name: 'channels',
    initialState,
    reducers: {
        setChannels(state, { payload }) {
            state.channels = payload;
        },
    },
});

export const { setChannels } = channelsSlice;
export default channelsSlice.reducer;
