import React from 'react';
import cn from 'classnames';

const Channel = ({ name, removable, id, currentChannelId }) => {
  const btnClass = cn('w-100', 'rounded-0', 'text-start', 'btn', {
    'btn-secondary': id === currentChannelId,
  });
  //   console.log(name, removable, id, currentChannelId);
  if (removable) {
    return (
      <div role="group" className="d-flex dropdown btn-group">
        <button type="button" className={btnClass}>
          <span className="me-1">#</span>
          {name}
        </button>
        <button
          type="button"
          id="react-aria5523499611-2"
          aria-expanded="false"
          className="flex-grow-0 dropdown-toggle dropdown-toggle-split btn btn-secondary">
          <span className="visually-hidden">Управление каналом</span>
        </button>
        <div
          x-placement="bottom-start"
          aria-labelledby="react-aria5523499611-2"
          className="dropdown-menu"
          data-popper-reference-hidden="false"
          data-popper-escaped="false"
          data-popper-placement="bottom-start">
          <a
            data-rr-ui-dropdown-item=""
            className="dropdown-item"
            role="button"
            tabindex="0"
            href="#">
            Удалить
          </a>
          <a
            data-rr-ui-dropdown-item=""
            className="dropdown-item"
            role="button"
            tabindex="0"
            href="#">
            Переименовать
          </a>
        </div>
      </div>
    );
  }
  return (
    <>
      <button type="button" className={btnClass}>
        <span className="me-1">#</span>
        {name}
      </button>
    </>
  );
};

export default Channel;
